import React, { useState } from "react";
import { Button } from "./Button";
import { Link } from "react-router-dom";
import "./Navbar.css";
import Dropdown from "./Dropdown";
import { Img } from "react-image";
import jdlogo from "../images/jdlogo.svg";
import { faBars } from "@fortawesome/free-solid-svg-icons";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useHistory } from 'react-router-dom';
import { useOktaAuth } from '@okta/okta-react';

function Navbar() {
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };

  const history = useHistory();
  const { oktaAuth, authState } = useOktaAuth();

  if (!authState) return null;

  const login = async () => history.push('/login');

  const logout = async () => oktaAuth.signOut();

  const button = authState.isAuthenticated ?
    <button onClick={logout}>Logout</button> :
    <button onClick={login}>Login</button>;

  return (
    <>
      <nav className="navbar-aux">
        <Link to="/" className="navbar-logo-aux" onClick={closeMobileMenu}>
        <Img src={jdlogo} />
        </Link>
        <div className="menu-icon" onClick={handleClick}>

          <FontAwesomeIcon icon={click ? faTimes : faBars} style={{ color: '#367c2b' }} />
        </div>
        <ul className={click ? "nav-menu-aux active" : "nav-menu-aux"}>
          <li className="nav-item-aux">
            <Link to="/" className="nav-links-aux" onClick={closeMobileMenu}>
              Home
            </Link>
          </li>
          <li
            className="nav-item-aux"
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <Link
              to="/schedules"
              className="nav-links-aux"
              onClick={closeMobileMenu}
            >
              Schedules Lines <i className="fas fa-caret-down" />
            </Link>
            {dropdown && <Dropdown />}
          </li>
          <li className="nav-item-aux">
            <Link to="/help" className="nav-links-aux" onClick={closeMobileMenu}>
              Help
            </Link>
          </li>
          <li className="nav-item-aux">
            <Link
              to="/contact-us"
              className="nav-links-aux"
              onClick={closeMobileMenu}
            >
              Contact Us
            </Link>
          </li>
          <li>
            <Link
              to="/sign-up"
              className="nav-links-mobile"
              onClick={closeMobileMenu}
            >
              Sign Up
            </Link>
          </li>
          <li>
          {button}
          </li>
        </ul>
        <Button />
      </nav>
    </>
  );
}

export default Navbar;
