import React from "react";
import MaterialBox from '@material-ui/core/Box';
import { BoxProps as MaterialBoxProps } from '@material-ui/core/Box';

export interface BoxProps extends MaterialBoxProps { };

export const Box = React.forwardRef<any, BoxProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      // @ts-ignore Ongoing issue with adding correct typing to Box component: https://github.com/mui-org/material-ui/issues/17010
      <MaterialBox ref={ref} {...props}>
        {children}
      </MaterialBox>
    );
  });
