import React from "react";
import MaterialBreadcrumbs from '@material-ui/core/Breadcrumbs';
import { withStyles } from '@material-ui/core/styles';
import { BreadcrumbsProps as MaterialBreadcrumbsProps } from '@material-ui/core/Breadcrumbs';

export interface BreadcrumbsProps extends MaterialBreadcrumbsProps { };

const StyledBreadcrumbs = withStyles((theme) => ({
  root: {
    fontSize: theme.typography.button.fontSize
  },
  li: {
    "& .MuiTypography-root": {
      fontSize: theme.typography.button.fontSize
    }
  }
}))(MaterialBreadcrumbs);

export const Breadcrumbs = React.forwardRef<any, BreadcrumbsProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledBreadcrumbs ref={ref} {...props}>
        {children}
      </StyledBreadcrumbs>
    );
  });
