import React from "react";
import MaterialCollapse from '@material-ui/core/Collapse';
import { CollapseProps as MaterialCollapseProps } from '@material-ui/core/Collapse';

export interface CollapseProps extends MaterialCollapseProps { };

export const Collapse = React.forwardRef<any, CollapseProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialCollapse ref={ref} {...props}>
        {children}
      </MaterialCollapse>
    );
  });
