import React from "react";
import MaterialContainer from '@material-ui/core/Container';
import { withStyles } from '@material-ui/core/styles';
import { ContainerProps as MaterialContainerProps } from '@material-ui/core/Container';

export interface ContainerProps extends MaterialContainerProps { };

const StyledContainer = withStyles((theme) => ({
  root: {
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(6)
    }
  }
}))(MaterialContainer);

export const Container = React.forwardRef<any, ContainerProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledContainer ref={ref} {...props}>
        {children}
      </StyledContainer>
    );
  });
