import React from "react";
import MaterialScopedCssBaseline from '@material-ui/core/ScopedCssBaseline';
import { ScopedCssBaselineProps as MaterialScopedCssBaselineProps } from '@material-ui/core/ScopedCssBaseline';

export interface ScopedCssBaselineProps extends MaterialScopedCssBaselineProps { };

export const ScopedCssBaseline = React.forwardRef<any, ScopedCssBaselineProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialScopedCssBaseline ref={ref} {...props}>
        {children}
      </MaterialScopedCssBaseline>
    );
  });
