import React from "react";
import MaterialDivider from '@material-ui/core/Divider';
import { DividerProps as MaterialDividerProps } from '@material-ui/core/Divider';

export interface DividerProps extends MaterialDividerProps { };

export const Divider = React.forwardRef<any, DividerProps>(
  (
    {
      ...props
    },
    ref
  ) => {
    return (
      <MaterialDivider ref={ref} {...props} />
    );
  });
