import React from "react";
import MaterialFormControl from '@material-ui/core/FormControl';
import { FormControlProps as MaterialFormControlProps } from '@material-ui/core/FormControl';

export interface FormControlProps extends MaterialFormControlProps { };

export const FormControl = React.forwardRef<any, FormControlProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialFormControl ref={ref} {...props}>
        {children}
      </MaterialFormControl>
    );
  });
