import React from "react";
import MaterialFormGroup from '@material-ui/core/FormGroup';
import { FormGroupProps as MaterialFormGroupProps } from '@material-ui/core/FormGroup';

export interface FormGroupProps extends MaterialFormGroupProps { };

export const FormGroup = React.forwardRef<any, FormGroupProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialFormGroup ref={ref} {...props}>
        {children}
      </MaterialFormGroup>
    );
  });
