import React from "react";
import MaterialFormHelperText from '@material-ui/core/FormHelperText';
import { FormHelperTextProps as MaterialFormHelperTextProps } from '@material-ui/core/FormHelperText';

export interface FormHelperTextProps extends MaterialFormHelperTextProps { };

export const FormHelperText = React.forwardRef<any, FormHelperTextProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialFormHelperText ref={ref} {...props}>
        {children}
      </MaterialFormHelperText>
    );
  });
