import React from "react";
import { withStyles } from '@material-ui/core/styles';
import MaterialFormLabel from '@material-ui/core/FormLabel';
import { FormLabelProps as MaterialFormLabelProps } from '@material-ui/core/FormLabel';

export interface FormLabelProps extends MaterialFormLabelProps { };

const StyledFormLabel = withStyles((theme) => ({
  root: {
    color: theme.palette.grey[900],
    '&.Mui-focused': {
      color: theme.palette.text.primary,
    },
    marginBottom: theme.spacing(1),
    fontWeight: theme.typography.button.fontWeight,
    fontSize: theme.typography.button.fontSize,
  }
}))(MaterialFormLabel);

export const FormLabel = React.forwardRef<any, FormLabelProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledFormLabel ref={ref} {...props}>
        {children}
      </StyledFormLabel>
    );
  });
