import React from "react";
import MaterialGrid from '@material-ui/core/Grid';
import { GridProps as MaterialGridProps } from '@material-ui/core/Grid';

export interface GridProps extends MaterialGridProps { };

export const Grid = React.forwardRef<any, GridProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialGrid ref={ref} {...props}>
        {children}
      </MaterialGrid>
    );
  });
