import React from "react";
import MaterialHidden from '@material-ui/core/Hidden';
import { HiddenProps as MaterialHiddenProps } from '@material-ui/core/Hidden';

export interface HiddenProps extends MaterialHiddenProps {
  /* The content of the component */
  children: React.ReactNode;
}


export const Hidden = (
  {
    children,
    ...props
  }: HiddenProps) => {
  return (
    <MaterialHidden {...props}>
      {children}
    </MaterialHidden>
  );
};
