import React from "react";
import MaterialInputAdornment from '@material-ui/core/InputAdornment';
import { InputAdornmentProps as MaterialInputAdornmentProps } from '@material-ui/core/InputAdornment';

export interface InputAdornmentProps extends MaterialInputAdornmentProps { };

export const InputAdornment = React.forwardRef<any, InputAdornmentProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialInputAdornment ref={ref} {...props}>
        {children}
      </MaterialInputAdornment>
    );
  });
