import React from "react";
import MaterialLink from '@material-ui/core/Link';
import { LinkProps as MaterialLinkProps } from '@material-ui/core/Link';
import { css } from "@emotion/react";

export interface LinkProps extends MaterialLinkProps { };

const linkStyles = css`
  color: var(--fuel-color-text-link);

  &:hover {
    color: var(--fuel-color-text-link-hovered);
  }
  &.Mui-focusVisible {
    box-shadow: var(--fuel-shadow-focused);
    outline: none;
  }
`;

export const Link = React.forwardRef<any, LinkProps>(
  (
    {
      children,
      underline = 'always',
      ...props
    },
    ref
  ) => {
    return (
      <MaterialLink css={linkStyles} underline={underline} ref={ref} {...props}>
        {children}
      </MaterialLink>
    );
  });

