import React from "react";
import MaterialList from '@material-ui/core/List';
import { ListProps as MaterialListProps } from '@material-ui/core/List';

export interface ListProps extends MaterialListProps { };

export const List = React.forwardRef<any, ListProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialList ref={ref} {...props}>
        {children}
      </MaterialList>
    );
  });
