import React from "react";
import MaterialListItemAvatar from '@material-ui/core/ListItemAvatar';
import { ListItemAvatarProps as MaterialListItemAvatarProps } from '@material-ui/core/ListItemAvatar';

export interface ListItemAvatarProps extends MaterialListItemAvatarProps { };

export const ListItemAvatar = React.forwardRef<any, ListItemAvatarProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialListItemAvatar ref={ref} {...props}>
        {children}
      </MaterialListItemAvatar>
    );
  });
