import React from "react";
import MaterialListItemIcon from '@material-ui/core/ListItemIcon';
import { withStyles } from '@material-ui/core/styles';
import { ListItemIconProps as MaterialListItemIconProps } from '@material-ui/core/ListItemIcon';

export interface ListItemIconProps extends MaterialListItemIconProps { };

const StyledListItemIcon = withStyles((theme) => ({
  root: {
    minWidth: '40px',
    color: theme.palette.grey[400]
  }
}))(MaterialListItemIcon);

export const ListItemIcon = React.forwardRef<any, ListItemIconProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledListItemIcon ref={ref} {...props}>
        {children}
      </StyledListItemIcon>
    );
  });
