import React from "react";
import MaterialListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import { ListItemSecondaryActionProps as MaterialListItemSecondaryActionProps } from '@material-ui/core/ListItemSecondaryAction';

export interface ListItemSecondaryActionProps extends MaterialListItemSecondaryActionProps { };

export const ListItemSecondaryAction = React.forwardRef<any, ListItemSecondaryActionProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialListItemSecondaryAction ref={ref} {...props}>
        {children}
      </MaterialListItemSecondaryAction>
    );
  });
