import React from "react";
import MaterialListItemText from '@material-ui/core/ListItemText';
import { ListItemTextProps as MaterialListItemTextProps } from '@material-ui/core/ListItemText';

export interface ListItemTextProps extends MaterialListItemTextProps { };

export const ListItemText = React.forwardRef<any, ListItemTextProps>(
  (
    {
      ...props
    },
    ref
  ) => {
    return (
      <MaterialListItemText ref={ref} {...props} />
    );
  });
