import React from "react";
import MaterialListSubheader from '@material-ui/core/ListSubheader';
import { ListSubheaderProps as MaterialListSubheaderProps } from '@material-ui/core/ListSubheader';
import { withStyles } from '@material-ui/core/styles';

export interface ListSubheaderProps extends MaterialListSubheaderProps { };

const StyledListSubheader = withStyles({
  root: {
    lineHeight: '40px'
  }
})(MaterialListSubheader);

export const ListSubheader = React.forwardRef<any, ListSubheaderProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledListSubheader ref={ref} {...props}>
        {children}
      </StyledListSubheader>
    );
  });
