import React from "react";
import MaterialMenuList from '@material-ui/core/MenuList';
import { MenuListProps as MaterialMenuListProps } from '@material-ui/core/MenuList';

export interface MenuListProps extends MaterialMenuListProps { };

export const MenuList = React.forwardRef<any, MenuListProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialMenuList ref={ref} {...props}>
        {children}
      </MaterialMenuList>
    );
  });
