import React from "react";
import MaterialPaper from '@material-ui/core/Paper';
import { PaperProps as MaterialPaperProps } from '@material-ui/core/Paper';
import { css } from "@emotion/react";

type FuelBorderRadius = 'none' | 'sm' | 'md' | 'lg' | 'xl' | '2xl' | 'full';

export interface PaperProps extends MaterialPaperProps {
  /* Border Radius */
  borderRadius?: FuelBorderRadius;
};

export const Paper = React.forwardRef<any, PaperProps>(
  (
    {
      children,
      borderRadius = 'lg',
      square,
      ...props
    },
    ref
  ) => {

    const borderRadiusStyles = !square && borderRadius && css`
      border-radius: var(--fuel-border-radius-${borderRadius})
    `;

    return (
      <MaterialPaper square={square} css={borderRadiusStyles} ref={ref} {...props}>
        {children}
      </MaterialPaper>
    );
  });
