import React from "react";
import MaterialRadioGroup from '@material-ui/core/RadioGroup';
import { RadioGroupProps as MaterialRadioGroupProps } from '@material-ui/core/RadioGroup';

export interface RadioGroupProps extends MaterialRadioGroupProps { };

export const RadioGroup = React.forwardRef<any, RadioGroupProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialRadioGroup ref={ref} {...props}>
        {children}
      </MaterialRadioGroup>
    );
  });
