import React from "react";
import MaterialAlert from '@material-ui/lab/Alert';
import { AlertProps as MaterialAlertProps } from '@material-ui/lab/Alert';
import { withStyles } from '@material-ui/core/styles';

export interface AlertProps extends MaterialAlertProps { };

// TODO: Refactor this to include full font. Strange issue here where
// wrong font family is being applied on the 'Success' variant when using theme object.
const StyledAlert = withStyles(() => ({
  root: {
    fontFamily: `Open Sans !important`
  }
}))(MaterialAlert);

export const Alert = React.forwardRef<any, AlertProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledAlert ref={ref} {...props}>
        {children}
      </StyledAlert>
    );
  });
