import React from "react";
import MaterialAlertTitle from '@material-ui/lab/AlertTitle';
import { AlertTitleProps as MaterialAlertTitleProps } from '@material-ui/lab/AlertTitle';
import { withStyles } from '@material-ui/core/styles';

export interface AlertTitleProps extends MaterialAlertTitleProps { };

const StyledAlertTitle = withStyles(() => ({
  root: {
    fontWeight: 600,
  }
}))(MaterialAlertTitle);

export const AlertTitle = React.forwardRef<any, AlertTitleProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <StyledAlertTitle ref={ref} {...props}>
        {children}
      </StyledAlertTitle>
    );
  });
