import React from "react";
import MaterialBadge from '@material-ui/core/Badge';
import { BadgeProps as MaterialBadgeProps } from '@material-ui/core/Badge';

export interface BadgeProps extends MaterialBadgeProps { };

export const Badge = React.forwardRef<any, BadgeProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialBadge ref={ref} {...props}>
        {children}
      </MaterialBadge>
    );
  });
