import React from "react";
import MaterialFab from '@material-ui/core/Fab';
import classNames from 'classnames';
import styles from './Button.module.scss';
import { FabProps as MaterialFabProps } from '@material-ui/core/Fab';

export interface FabProps extends MaterialFabProps {
  /* Add custom classes where needed */
  className?: string,
}

export const Fab = React.forwardRef<any, FabProps>(
  (
    {
      children,
      className,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialFab ref={ref} className={classNames(styles.button, styles.fab, className)} {...props}>
        {children}
      </MaterialFab>
    );
  });
