import React, { FC } from "react";
import MaterialButtonGroup from '@material-ui/core/ButtonGroup';
import { ButtonGroupProps as MaterialButtonGroupProps } from '@material-ui/core/ButtonGroup';

export interface ButtonGroupProps extends MaterialButtonGroupProps { };

export const ButtonGroup = React.forwardRef<any, ButtonGroupProps>(
  (
    {
      children,
      ...props
    },
    ref
  ) => {
    return (
      <MaterialButtonGroup ref={ref} {...props}>
        {children}
      </MaterialButtonGroup>
    );
  });
