import React from "react";
import MaterialChip from '@material-ui/core/Chip';
import classNames from 'classnames';
import styles from './Chip.module.scss';
import { ChipProps as MaterialChipProps } from '@material-ui/core/Chip';

type FuelVariant = 'informational' | 'success' | 'attention' | 'warning' | 'critical';

export interface ChipProps extends MaterialChipProps {
  /* Fuel specific variations */
  fuelVariant?: FuelVariant,
  /* Add custom classes where needed */
  className?: string,
}

export const Chip = React.forwardRef<any, ChipProps>(
  (
    {
      fuelVariant,
      className,
      size,
      ...props
    },
    ref
  ) => {
    const chipClasses = classNames(
      fuelVariant === 'informational' && styles.chipInformational,
      fuelVariant === 'success' && styles.chipSuccess,
      fuelVariant === 'attention' && styles.chipAttention,
      fuelVariant === 'warning' && styles.chipWarning,
      fuelVariant === 'critical' && styles.chipCritical,
      styles.chip,
      className
    );

    return (
      <MaterialChip ref={ref} size={size ? size : 'small'} className={chipClasses} {...props} />
    );
  });
