import React from "react";
import MaterialCircularProgress from '@material-ui/core/CircularProgress';
import { CircularProgressProps as MaterialCircularProgressProps } from '@material-ui/core/CircularProgress';

export interface CircularProgressProps extends MaterialCircularProgressProps { };

export const CircularProgress = React.forwardRef<any, CircularProgressProps>(
  (
    {
      ...props
    },
    ref
  ) => {
    return (
      <MaterialCircularProgress ref={ref} {...props} />
    );
  });
