import React from "react";
import MaterialLinearProgress from '@material-ui/core/LinearProgress';
import { LinearProgressProps as MaterialLinearProgressProps } from '@material-ui/core/LinearProgress';

export interface LinearProgressProps extends MaterialLinearProgressProps { };

export const LinearProgress = React.forwardRef<any, LinearProgressProps>(
  (
    {
      ...props
    },
    ref
  ) => {
    return (
      <MaterialLinearProgress ref={ref} {...props} />
    );
  });
