import React from "react";
import MaterialSwitch from '@material-ui/core/Switch';
import { SwitchProps as MaterialSwitchProps } from '@material-ui/core/Switch';

export interface SwitchProps extends MaterialSwitchProps { };

export const Switch = React.forwardRef<any, SwitchProps>(
  (
    {
      ...props
    },
    ref
  ) => {
    return (
      <MaterialSwitch ref={ref} {...props} />
    );
  });

