import React, { FC } from 'react';
import { ThemeProvider, ThemeProviderProps, jdGreen } from '../';
import { render, RenderOptions } from "@testing-library/react";

/**
 * Wrapper for React Testing Library render() to pass in
 * Fuel ThemeProvider. Default theme is set to 'jdGreen'
 *
 */
const FuelThemeProvider: FC<ThemeProviderProps> = ({ children }) => {
  return (
    <ThemeProvider theme={jdGreen}>
      {children}
    </ThemeProvider>
  )
}

const customRender = (
  ui: React.ReactElement,
  options?: Omit<RenderOptions, 'queries'>
) => render(ui, { wrapper: FuelThemeProvider, ...options })

export * from '@testing-library/react'

export { customRender as render }

