$(function () {

    var btn = document.getElementsByClassName('copy-code');
    var clipboard = new ClipboardJS(btn, {  // eslint-disable-line no-unused-vars
        target: function (trigger) {
            return trigger.nextElementSibling.firstChild
        }
    });

    var iconClipboard = new ClipboardJS('.copy-svg'); // eslint-disable-line no-unused-vars

    $('.copy-svg').on('click', function () {
        var copySvgWidth = $('.copy-svg').width();
        $(".copy-svg:contains('Copied!')").html('Copy Code');
        $(this).html('Copied!').width(copySvgWidth);
    });

    // Scrollspy Toggle Buttons
    $('.toggle-right.mobile-option').click(function () {
        if ($('.mobile__content').length) {
            $('.mobile__content').addClass('active');
            $('.desktop__content .demo').removeClass('active');
        }
        $('.demo').addClass('mobile-active');
        $('.code-content').addClass('mobile-active');

        //Changing font guidelines based on if desktop or mobile toggle is selected
        $('#font-guidelines').addClass('mobile-active');
        $('.desktop__guidelines').hide();
        $('.mobile__guidelines').show();

        $(this).siblings(".background-toggle").addClass('toggle-active');
    });

    $('.toggle-right.black-option').click(function () {
        $(this).siblings(".background-toggle").addClass('toggle-active');
        $('html').removeClass('uxf-black-yellow uxf-jdf').addClass('uxf-black-yellow uxf-2018');
    });

    $('.toggle-left.desktop-option').click(function () {
        if ($('.mobile__content').length) {
            $('.desktop__content .demo').addClass('active');
            $('.mobile__content').removeClass('active');
        }
        $('.demo').removeClass('mobile-active');
        $('.code-content').removeClass('mobile-active');

        //Changing font guidelines based on if desktop or mobile toggle is selected
        $('#font-guidelines').removeClass('mobile-active');
        $('.mobile__guidelines').hide();
        $('.desktop__guidelines').show();

        $(this).siblings(".background-toggle").removeClass('toggle-active');
    });

    $('.toggle-left.green-option').click(function () {
        $(this).siblings(".background-toggle").removeClass('toggle-active');
        $('html').removeClass('uxf-black-yellow uxf-jdf').addClass('uxf-2018');
    });

    $(function () {
        // Smooth Scroll Component Pages
        $('.scrollspy__header').each(function () {
            $(this).click(function () {
                $("html, body").animate({
                    scrollTop: 0
                }, "slow");
            });
        });

        // Portfolio Projects
        $('.example-project').mouseleave(function (e) {
            $(this).find('.example-hover').removeClass('on');
            e.preventDefault();
            e.stopPropagation();
        });
        $('.example').mouseenter(function (e) {
            $(this).closest('.example-project').find('.example-hover').addClass('on');
            e.preventDefault();
            e.stopPropagation();
        });

    });

    $('.navbar-toggle').click(function () {
        $("body").addClass('has-backdrop');
        $("html").addClass('has-backdrop');
    })
    $('.navbar-back').click(function () {
        $("body").removeClass('has-backdrop');
        $("html").removeClass('has-backdrop');
    })

    $(document).on('nfFormReady', function (e, layoutView) {    // eslint-disable-line no-unused-vars
        $('<span></span>').insertAfter('.uxf .nf-field-element input[type=checkbox]');
        $('<span></span>').insertAfter('.uxf .nf-field-element input[type=radio]');
    });

});

module.exports = $UXF;
